tables.table_schema:
CREATE TABLE public_officials (
    id INT PRIMARY KEY,
    name VARCHAR(50),
    party_affiliation VARCHAR(50),
    position VARCHAR(50),
    office_location VARCHAR(100)
);

CREATE TABLE voting_records (
    record_id INT PRIMARY KEY,
    official_id INT,
    vote_decision VARCHAR(10),
    vote_date DATE,
    motion_id INT,
    FOREIGN KEY (official_id) REFERENCES public_officials(id)
);

CREATE TABLE motions (
    motion_id INT PRIMARY KEY,
    motion_description TEXT,
    vote_result VARCHAR(10)
);

CREATE TABLE public_officials (
  id INT PRIMARY KEY,
  name VARCHAR(255) NOT NULL,
  party VARCHAR(50) NOT NULL,
  district VARCHAR(50) NOT NULL
);

CREATE TABLE committees (
  committee_id INT PRIMARY KEY,
  committee_name VARCHAR(255) NOT NULL
);
