tables.table_schema:
CREATE TABLE citizen_feedback (
    feedback_id SERIAL PRIMARY KEY,
    citizen_name VARCHAR(50) NOT NULL,
    feedback_text TEXT NOT NULL,
    submission_date TIMESTAMP NOT NULL
);

CREATE TABLE government_officials (
    official_id SERIAL PRIMARY KEY,
    official_name VARCHAR(50) NOT NULL,
    department VARCHAR(50) NOT NULL,
    office_address VARCHAR(100) NOT NULL
);

CREATE TABLE Citizens (
    citizen_id UUID PRIMARY KEY,
    name VARCHAR(50),
    email VARCHAR(100),
    address VARCHAR(150)
);

CREATE TABLE Officials (
    official_id UUID PRIMARY KEY,
    name VARCHAR(50),
    department VARCHAR(50),
    contact_number VARCHAR(15)
);

CREATE TABLE Feedback (
    feedback_id UUID PRIMARY KEY,
    citizen_id UUID REFERENCES Citizens(citizen_id),
    feedback_text TEXT,
    timestamp TIMESTAMP
);
