tables.table_schema:
CREATE TABLE public_meetings_and_hearings (
  meeting_id UUID PRIMARY KEY,
  meeting_date TIMESTAMP,
  meeting_location VARCHAR(255),
  meeting_description TEXT
);

CREATE TABLE public_meetings (
    meeting_id UUID PRIMARY KEY,
    meeting_title TEXT NOT NULL,
    meeting_date DATE NOT NULL,
    meeting_location TEXT NOT NULL
);

CREATE TABLE meeting_attendees (
    attendee_id UUID PRIMARY KEY,
    meeting_id UUID REFERENCES public_meetings(meeting_id),
    attendee_name TEXT NOT NULL,
    attendee_email TEXT
);

CREATE TABLE meeting_topics (
    topic_id UUID PRIMARY KEY,
    meeting_id UUID REFERENCES public_meetings(meeting_id),
    topic_title TEXT NOT NULL,
    topic_description TEXT
);

CREATE TABLE meeting_comments (
    comment_id UUID PRIMARY KEY,
    meeting_id UUID REFERENCES public_meetings(meeting_id),
    comment_text TEXT NOT NULL,
    commenter_name TEXT NOT NULL
);
