tables.table_schema:
CREATE TABLE public_opinion_polls (
id SERIAL PRIMARY KEY,
question TEXT,
response TEXT
);

CREATE TABLE opinion_polls (
  id INT PRIMARY KEY,
  question TEXT,
  answer TEXT,
  respondent_name TEXT
);

CREATE TABLE surveys (
  id INT PRIMARY KEY,
  survey_name TEXT,
  question TEXT,
  answer_options JSON
);

CREATE TABLE surveys (
    id INT PRIMARY KEY,
    question TEXT,
    options JSONB
);

CREATE TABLE policy_decisions (
    decision_id INT PRIMARY KEY,
    decision_description TEXT,
    decision_date DATE,
    implemented BOOLEAN
);
