tables.table_schema:
CREATE TABLE social_media_users (
    user_id INT PRIMARY KEY,
    user_name VARCHAR(50),
    user_location VARCHAR(50),
    followers_count INT
);

CREATE TABLE sentiment_analysis (
    analysis_id INT PRIMARY KEY,
    user_id INT REFERENCES social_media_users(user_id),
    sentiment_score FLOAT,
    analysis_date DATE
);

CREATE TABLE social_media_users (
    user_id INT PRIMARY KEY,
    username VARCHAR(50),
    email VARCHAR(100)
);

CREATE TABLE social_media_posts (
    post_id INT PRIMARY KEY,
    user_id INT,
    post_content TEXT,
    post_date TIMESTAMP
);

CREATE TABLE sentiment_analysis (
    post_id INT,
    sentiment_score FLOAT,
    sentiment_label VARCHAR(20)
);

CREATE TABLE public.sentiment_data (
    id INT PRIMARY KEY,
    user_id INT,
    message TEXT,
    sentiment_score FLOAT
);

CREATE TABLE public.user_profile (
    user_id INT PRIMARY KEY,
    username TEXT,
    email TEXT
);

CREATE TABLE public.post_reaction (
    post_id INT,
    user_id INT,
    reaction_type TEXT,
    timestamp TIMESTAMP
);

CREATE TABLE public.sentiment_analysis (
    analysis_id INT PRIMARY KEY,
    post_id INT,
    sentiment_score FLOAT,
    analysis_date DATE
);

CREATE TABLE users (
    user_id INT PRIMARY KEY,
    username VARCHAR(50) NOT NULL,
    email VARCHAR(100) NOT NULL,
    join_date TIMESTAMP
);

CREATE TABLE posts (
    post_id INT PRIMARY KEY,
    user_id INT,
    post_content VARCHAR(255) NOT NULL,
    post_date TIMESTAMP
);

CREATE TABLE likes (
    like_id INT PRIMARY KEY,
    post_id INT,
    user_id INT,
    like_date TIMESTAMP
);

CREATE TABLE hashtags (
    hashtag_id INT PRIMARY KEY,
    hashtag_text VARCHAR(50) NOT NULL
);
