tables.table_schema:
CREATE TABLE voter_records (
  voter_id INT PRIMARY KEY,
  voter_name VARCHAR(50),
  date_of_birth DATE,
  registration_status BOOLEAN
);

CREATE TABLE voter_history (
  history_id INT PRIMARY KEY,
  voter_id INT REFERENCES voter_records(voter_id),
  registration_date DATE,
  voting_status BOOLEAN
);

CREATE TABLE voters (
    voter_id INT PRIMARY KEY,
    voter_name VARCHAR(255),
    voter_address VARCHAR(255),
    voter_age INT
);

CREATE TABLE voting_districts (
    district_id INT PRIMARY KEY,
    district_name VARCHAR(255),
    district_location VARCHAR(255)
);

CREATE TABLE voter_contact (
    contact_id SERIAL PRIMARY KEY,
    voter_id INT REFERENCES voters(voter_id) ON DELETE CASCADE,
    email VARCHAR(100),
    phone_number VARCHAR(20)
);
