tables.table_schema:
CREATE TABLE candidate_profiles (
  id INT PRIMARY KEY,
  name VARCHAR,
  age INT,
  bio TEXT
);

CREATE TABLE platform_profiles (
  candidate_id INT REFERENCES candidate_profiles(id),
  platform_id INT,
  platform_name VARCHAR,
  platform_username VARCHAR
);

CREATE TABLE skills (
    skill_id INT PRIMARY KEY,
    candidate_id INT,
    skill_name VARCHAR(50),
    proficiency_level VARCHAR(20)
);

CREATE TABLE skills (
    skill_id UUID PRIMARY KEY,
    candidate_id UUID REFERENCES candidate_profile(candidate_id),
    skill_name VARCHAR(50),
    proficiency VARCHAR(20)
);

CREATE TABLE social_media_profiles (
    social_id UUID PRIMARY KEY,
    candidate_id UUID REFERENCES candidate_profile(candidate_id),
    platform VARCHAR(50),
    profile_url VARCHAR(200)
);
