    contribution_id INT PRIMARY KEY,
    contributor_name VARCHAR(255),
    contribution_amount DECIMAL,
    contribution_date DATE
);

CREATE TABLE contributions (
  id INT PRIMARY KEY,
  contributor_name TEXT,
  amount DECIMAL,
  date DATE
);

CREATE TABLE contributions (
    contributor_id INT PRIMARY KEY,
    amount DECIMAL,
    date DATE,
    campaign_id INT
);

CREATE TABLE campaigns (
    campaign_id INT PRIMARY KEY,
    campaign_name VARCHAR,
    start_date DATE,
    end_date DATE
);

CREATE TABLE recipients (
    id SERIAL PRIMARY KEY,
    recipient_name TEXT NOT NULL,
    office TEXT NOT NULL,
    party_affiliation TEXT
);

CREATE TABLE contributors (
    contributor_id INT PRIMARY KEY,
    contributor_name STRING,
    contribution_amount DECIMAL
);

CREATE TABLE campaigns (
    campaign_id INT PRIMARY KEY,
    campaign_name STRING,
    candidate_id INT
);

CREATE TABLE expenditures (
    expenditure_id INT PRIMARY KEY,
    expenditure_description STRING,
    expenditure_amount DECIMAL
);

CREATE TABLE candidates (
    candidate_id INT PRIMARY KEY,
    candidate_name STRING,
    party_affiliation STRING
);

CREATE TABLE contribution_logs (
    log_id INT PRIMARY KEY,
    contributor_id INT,
    campaign_id INT,
    contribution_timestamp TIMESTAMP
);
