tables.table_schema:
CREATE TABLE polling_locations (
  location_id INT PRIMARY KEY,
  location_name VARCHAR(255) NOT NULL,
  location_address VARCHAR(255) NOT NULL,
  manager_name VARCHAR(100) NOT NULL
);

CREATE TABLE voting_machines (
  machine_id INT PRIMARY KEY,
  machine_type VARCHAR(50),
  location_id INT,
  FOREIGN KEY (location_id) REFERENCES polling_locations(location_id)
);

CREATE TABLE VotingMachines (
  machine_id UUID PRIMARY KEY,
  location_id UUID REFERENCES PollingLocations(location_id),
  machine_type STRING,
  status STRING
);

CREATE TABLE MachineLogs (
  log_id UUID PRIMARY KEY,
  machine_id UUID REFERENCES VotingMachines(machine_id),
  log_timestamp TIMESTAMP,
  log_description STRING
);

CREATE TABLE machine_maintenance (
    maintenance_id INT PRIMARY KEY,
    machine_id INT,
    maintenance_type TEXT,
    maintenance_date DATE
);
