tables.table_schema:
CREATE TABLE absentee_and_early_voting (
    voter_id INT PRIMARY KEY,
    voter_name VARCHAR(50) NOT NULL,
    request_date DATE,
    submission_date DATE
);

CREATE TABLE absentee_requests (
    request_id INT PRIMARY KEY,
    voter_id INT,
    request_date DATE,
    status VARCHAR(10)
);

CREATE TABLE AbsenteeRequests (
  request_id INT PRIMARY KEY,
  voter_name VARCHAR(255),
  request_date DATE
);

CREATE TABLE AbsenteeSubmissions (
  submission_id INT PRIMARY KEY,
  request_id INT,
  submission_date DATE,
  FOREIGN KEY (request_id) REFERENCES AbsenteeRequests(request_id)
);

CREATE TABLE EarlyVotingRequests (
  early_request_id INT PRIMARY KEY,
  voter_name VARCHAR(255),
  request_date DATE
);

CREATE TABLE AbsenteeVotingRequest (
    request_id INT PRIMARY KEY,
    voter_id INT,
    request_date TIMESTAMP,
    request_status VARCHAR(50)
);

CREATE TABLE EarlyVotingRequest (
    request_id INT PRIMARY KEY,
    voter_id INT,
    request_date TIMESTAMP,
    request_status VARCHAR(50)
);

CREATE TABLE AbsenteeVotingSubmission (
    submission_id INT PRIMARY KEY,
    request_id INT,
    submission_date TIMESTAMP,
    submission_status VARCHAR(50)
);

CREATE TABLE EarlyVotingSubmission (
    submission_id INT PRIMARY KEY,
    request_id INT,
    submission_date TIMESTAMP,
    submission_status VARCHAR(50)
);

CREATE TABLE AbsenteeVotingRequests (
  voter_id INT,
  request_date DATE,
  request_reason TEXT
);

CREATE TABLE AbsenteeVotingSubmissions (
  submission_id INT,
  voter_id INT,
  submission_date DATE
);

CREATE TABLE EarlyVotingRequests (
  voter_id INT,
  request_date DATE,
  location TEXT
);

CREATE TABLE EarlyVotingSubmissions (
  submission_id INT,
  voter_id INT,
  submission_date DATE,
  location TEXT
);

CREATE TABLE VotingRecords (
  voter_id INT,
  voting_date DATE,
  voting_type TEXT
);
