tables.table_schema:
CREATE TABLE public_hearings (
    id INT PRIMARY KEY,
    date DATE,
    topic VARCHAR(255),
    location VARCHAR(255)
);

CREATE TABLE agendas (
    agenda_id INT PRIMARY KEY,
    agenda_title VARCHAR(255),
    hearing_id INT,
    FOREIGN KEY (hearing_id) REFERENCES hearings(hearing_id)
);

CREATE TABLE topics (
    topic_id INT PRIMARY KEY,
    topic_name VARCHAR(255),
    agenda_id INT,
    FOREIGN KEY (agenda_id) REFERENCES agendas(agenda_id)
);
