tables.table_schema:
CREATE TABLE policy_evaluations (
    evaluation_id UUID PRIMARY KEY,
    policy_name TEXT,
    evaluation_date DATE,
    evaluation_score FLOAT
);

CREATE TABLE policy_evaluations (
  id INT PRIMARY KEY,
  policy_name VARCHAR(100) NOT NULL,
  evaluation_date DATE,
  evaluation_result VARCHAR(255)
);

CREATE TABLE impact_assessments (
  id INT PRIMARY KEY,
  impact_id INT,
  assessment_date DATE,
  assessment_result TEXT
);

CREATE TABLE policy_roles (
    id INT PRIMARY KEY,
    role_name VARCHAR(255),
    description TEXT
);

CREATE TABLE evaluators(
	evaluator_id INT PRIMARY KEY,
	evaluator_name VARCHAR(100),
	evaluator_email VARCHAR(100),
	evaluation_count INT
);

CREATE TABLE policy_areas(
	policy_area_id INT PRIMARY KEY,
	policy_area_name VARCHAR(100),
	policy_description TEXT
);

CREATE TABLE evaluations_impacts(
	evaluation_id INT,
	assessment_id INT,
	evaluation_score FLOAT,
	impact_score FLOAT
);

CREATE TABLE evaluation_comments(
	evaluation_id INT,
	comment_text TEXT,
	evaluator_id INT
);
