tables.table_schema:
CREATE TABLE PolicyMakers (
	policy_maker_id UUID,
	policy_maker_name STRING,
	policy_risk_score INT,
	historical_data JSONB
);

CREATE TABLE PolicyMakers (
  id SERIAL PRIMARY KEY,
  name VARCHAR(50) NOT NULL,
  position VARCHAR(50) NOT NULL,
  affiliation VARCHAR(100) NOT NULL
);

CREATE TABLE PolicyRisks (
  id SERIAL PRIMARY KEY,
  risk_id VARCHAR(20) NOT NULL,
  risk_description TEXT,
  risk_type VARCHAR(50) NOT NULL
);

CREATE TABLE policies (
  policy_id UUID PRIMARY KEY,
  policy_name VARCHAR(50) NOT NULL,
  description TEXT,
  effective_date DATE
);

CREATE TABLE policy_data (
  data_id UUID PRIMARY KEY,
  policy_id UUID REFERENCES policies(policy_id),
  data_description TEXT,
  data_value FLOAT
);

CREATE TABLE policy_analysis (
  analysis_id UUID PRIMARY KEY,
  policy_id UUID REFERENCES policies(policy_id),
  analysis_date DATE,
  risk_score INT
);

CREATE TABLE PolicyMakers (
 policy_maker_id INT PRIMARY KEY,
 name VARCHAR(255),
 position VARCHAR(255),
 agency VARCHAR(255)
);

CREATE TABLE PolicyRisks (
 risk_id INT PRIMARY KEY,
 risk_description VARCHAR(255),
 risk_category VARCHAR(255),
 impact VARCHAR(255)
);

CREATE TABLE HistoricalData (
 data_id INT PRIMARY KEY,
 event_date DATE,
 data_description TEXT
);
