tables.table_schema:
CREATE TABLE public_opinion_sentiment_analysis (
    id INT PRIMARY KEY,
    social_media_post TEXT,
    sentiment_score DOUBLE PRECISION
);

CREATE TABLE social_media_data (
    post_id UUID PRIMARY KEY,
    post_content TEXT,
    sentiment_score FLOAT
);

CREATE TABLE public_opinion (
    opinion_id UUID PRIMARY KEY,
    post_id UUID REFERENCES social_media_data(post_id),
    opinion TEXT,
    sentiment_label VARCHAR(10)
);

CREATE TABLE SocialMediaPosts (
  post_id INT PRIMARY KEY,
  post_content TEXT,
  post_date TIMESTAMP,
  sentiment_score FLOAT
);

CREATE TABLE SentimentCategories (
  category_id INT PRIMARY KEY,
  category_name TEXT
);

CREATE TABLE sentiment_categories (category_id INT PRIMARY KEY,
                            category_name VARCHAR(50),
                            emotion_type VARCHAR(50);

CREATE TABLE public_opinion_sentiments (
    id INT PRIMARY KEY,
    username VARCHAR(50),
    sentiment_score FLOAT,
    post_timestamp TIMESTAMP
);

CREATE TABLE social_media_users (
    user_id INT PRIMARY KEY,
    user_name VARCHAR(50),
    account_type VARCHAR(20)
);

CREATE TABLE sentiment_categories (
    category_id INT PRIMARY KEY,
    category_name VARCHAR(50)
);

CREATE TABLE sentiment_analysis_results (
    result_id INT PRIMARY KEY,
    post_id INT,
    category_id INT,
    sentiment_score FLOAT
);
