tables.table_schema:
CREATE TABLE regulators (
    regulator_id INT PRIMARY KEY,
    regulator_name VARCHAR(50) NOT NULL,
    regulation_type VARCHAR(50) NOT NULL,
    department VARCHAR(50) NOT NULL
);

CREATE TABLE regulatory_data (
    id INT PRIMARY KEY,
    policy_maker_id INT,
    regulation_id INT,
    data TEXT
);

CREATE TABLE policy_maker (
    id INT PRIMARY KEY,
    name TEXT,
    email TEXT
);

CREATE TABLE policy_makers (
    id UUID PRIMARY KEY,
    first_name VARCHAR(50),
    last_name VARCHAR(50),
    title VARCHAR(50)
);

CREATE TABLE regulations (
    id UUID PRIMARY KEY,
    regulation_name VARCHAR(100),
    description TEXT,
    regulatory_body VARCHAR(100)
);

CREATE TABLE audit_logs (
    id UUID PRIMARY KEY,
    log_time TIMESTAMP,
    action_performed VARCHAR(100),
    user_id UUID
);

CREATE TABLE PolicyMakers (
    id SERIAL PRIMARY KEY,
    name VARCHAR(255) NOT NULL,
    organization VARCHAR(255) NOT NULL
);

CREATE TABLE Regulations (
    id SERIAL PRIMARY KEY,
    title VARCHAR(255) NOT NULL,
    description TEXT
);

CREATE TABLE AuditTrail (
    id SERIAL PRIMARY KEY,
    event_timestamp TIMESTAMP NOT NULL,
    event_description TEXT
);
