tables.table_schema:
CREATE TABLE campaign_finance_data (
 id INT PRIMARY KEY,
 candidate_name VARCHAR(255),
 donation_amount DECIMAL(10,2),
 donation_date DATE
);

CREATE TABLE campaign_contributors (
 id INT PRIMARY KEY,
 contributor_name VARCHAR(255),
 total_donations DECIMAL(15,2),
 last_donation_date DATE
);

CREATE TABLE campaign_contributions (
    contribution_id UUID PRIMARY KEY,
    donor_name VARCHAR(100),
    donation_amount DECIMAL,
    donation_date DATE
);

CREATE TABLE campaign_expenditures (
    expenditure_id UUID PRIMARY KEY,
    recipient_name VARCHAR(100),
    expenditure_amount DECIMAL,
    expenditure_date DATE
);

CREATE TABLE politician_records (
    politician_id UUID PRIMARY KEY,
    politician_name VARCHAR(100),
    political_party VARCHAR(50),
    election_year INT
);

CREATE TABLE campaign_contributions (
    contributor_id INT PRIMARY KEY,
    contributor_name VARCHAR(100),
    amount DECIMAL(10, 2),
    date_contributed DATE
);

CREATE TABLE campaigns (
    campaign_id INT PRIMARY KEY,
    campaign_name VARCHAR(100),
    start_date DATE,
    end_date DATE
);

CREATE TABLE candidates (
    candidate_id INT PRIMARY KEY,
    candidate_name VARCHAR(100),
    party_affiliation VARCHAR(50),
    election_year INT
);

CREATE TABLE expenditure_tracking (
    expenditure_id INT PRIMARY KEY,
    campaign_id INT,
    amount_spent DECIMAL(10, 2),
    expense_description TEXT
);

CREATE TABLE reporting (
    report_id INT PRIMARY KEY,
    campaign_id INT,
    report_date DATE,
    filing_status VARCHAR(50)
);
