tables.table_schema:
CREATE TABLE PolicyBefore (
    policy_id INT,
    policy_name VARCHAR(255),
    implementation_date DATE
);

CREATE TABLE PolicyAfter (
    policy_id INT,
    policy_name VARCHAR(255),
    implementation_date DATE,
    effectiveness_rating INT
);

CREATE TABLE policy_data (
  id INT PRIMARY KEY,
  policy_name VARCHAR(255),
  implementation_date DATE,
  assessment_date DATE
);

CREATE TABLE policies_before (
    policy_id INT PRIMARY KEY,
    policy_name VARCHAR(255),
    policy_type VARCHAR(100),
    implementation_date DATE
);

CREATE TABLE policies_after (
    policy_id INT PRIMARY KEY,
    policy_effectiveness VARCHAR(100),
    policy_impact VARCHAR(100),
    evaluation_date DATE
);

CREATE TABLE policy_makers (
    policymaker_id INT PRIMARY KEY,
    policymaker_name VARCHAR(255),
    policymaker_role VARCHAR(100)
);

CREATE TABLE policies (
	policy_id INT PRIMARY KEY,
	policy_name VARCHAR(255),
	policy_description TEXT
);
