tables.table_schema:
CREATE TABLE stage (
    stage_id int PRIMARY KEY,
    stage_name varchar(100)
);

CREATE TABLE Bill (
  bill_id SERIAL PRIMARY KEY,
  bill_number VARCHAR(255) NOT NULL,
  title TEXT NOT NULL,
  status VARCHAR(50) NOT NULL
);

CREATE TABLE BillStage (
  stage_id SERIAL PRIMARY KEY,
  stage_name VARCHAR(100) NOT NULL
);

CREATE TABLE BillProgress (
  progress_id SERIAL PRIMARY KEY,
  bill_id INT REFERENCES Bill(bill_id),
  stage_id INT REFERENCES BillStage(stage_id),
  date_progress TIMESTAMP NOT NULL
);

CREATE TABLE Legislator (
  legislator_id SERIAL PRIMARY KEY,
  name VARCHAR(255) NOT NULL,
  party VARCHAR(50) NOT NULL
);

CREATE TABLE BillAuthor (
  author_id SERIAL PRIMARY KEY,
  bill_id INT REFERENCES Bill(bill_id),
  legislator_id INT REFERENCES Legislator(legislator_id)
);
