tables.table_schema:
CREATE TABLE LegislationDatabase (
	politician_id INT,
	bill_id INT,
	vote VARCHAR(10),
	vote_date DATE
);

CREATE TABLE voting_records (
	record_id SERIAL PRIMARY KEY,
	bill_id INT,
	lawmaker_id INT,
	vote VARCHAR(10)
);

CREATE TABLE lawmakers (
	lawmaker_id SERIAL PRIMARY KEY,
	name VARCHAR(50),
	party VARCHAR(20),
	state VARCHAR(2)
);

CREATE TABLE bills (
	bill_id SERIAL PRIMARY KEY,
	title VARCHAR(100),
	description TEXT,
	introduced_date DATE
);

CREATE TABLE lawmakers (
    lawmaker_id INT PRIMARY KEY,
    lawmaker_name VARCHAR(255) NOT NULL,
    party_affiliation VARCHAR(50),
    district_id INT
);

CREATE TABLE bills (
    bill_id INT PRIMARY KEY,
    bill_title VARCHAR(255) NOT NULL,
    bill_category VARCHAR(50),
    bill_description TEXT
);

CREATE TABLE districts (
    district_id INT PRIMARY KEY,
    district_name VARCHAR(100),
    state_name VARCHAR(100)
);

CREATE TABLE lawmakers (
    id INT PRIMARY KEY,
    name VARCHAR(255) NOT NULL,
    party VARCHAR(50) NOT NULL,
    district VARCHAR(50) NOT NULL
);

CREATE TABLE bills (
    bill_id INT PRIMARY KEY,
    title VARCHAR(100) NOT NULL,
    description TEXT,
    status VARCHAR(50) NOT NULL
);

CREATE TABLE committees (
    committee_id INT PRIMARY KEY,
    name VARCHAR(100) NOT NULL,
    description TEXT
);

CREATE TABLE committee_assignments (
    assignment_id INT PRIMARY KEY,
    lawmaker_id INT NOT NULL,
    committee_id INT NOT NULL
);
