tables.table_schema:
CREATE TABLE introduced_bills (
    bill_id UUID PRIMARY KEY,
    bill_name VARCHAR(255) NOT NULL,
    introduction_date DATE NOT NULL
);

CREATE TABLE passed_bills (
    bill_id UUID PRIMARY KEY,
    bill_name VARCHAR(255) NOT NULL,
    passed_date DATE NOT NULL
);

CREATE TABLE Bills (
  bill_id INT PRIMARY KEY,
  bill_number VARCHAR(255),
  introduced_date DATE,
  passed_date DATE
);

CREATE TABLE Legislators (
  legislator_id INT PRIMARY KEY,
  name VARCHAR(255),
  party VARCHAR(255)
);

CREATE TABLE bills (
    bill_id INT PRIMARY KEY,
    bill_name TEXT NOT NULL,
    introduced_date DATE,
    passed_date DATE
);

CREATE TABLE legislators (
    legislator_id INT PRIMARY KEY,
    legislator_name TEXT NOT NULL
);

CREATE TABLE bills (
  bill_id INT PRIMARY KEY,
  bill_name VARCHAR(255) NOT NULL,
  introduced_date DATE,
  passed_date DATE
);

CREATE TABLE bill_sponsors (
  sponsor_id INT PRIMARY KEY,
  sponsor_name VARCHAR(255) NOT NULL,
  bill_id INT,
  FOREIGN KEY (bill_id) REFERENCES bills(bill_id)
);

CREATE TABLE legislators (
  legislator_id INT PRIMARY KEY,
  legislator_name VARCHAR(255) NOT NULL,
  party_affiliation VARCHAR(50),
  district_number INT
);
