tables.table_schema:
CREATE TABLE officials (
  id INT PRIMARY KEY,
  name VARCHAR(50),
  level VARCHAR(20),
  contact_info VARCHAR(100)
);

CREATE TABLE government_levels (
  level_id INT PRIMARY KEY,
  level_name VARCHAR(50),
  description TEXT
);

CREATE TABLE elected_officials (
  id SERIAL PRIMARY KEY,
  name VARCHAR(100) NOT NULL,
  title VARCHAR(100) NOT NULL,
  level_of_government VARCHAR(50) NOT NULL
);

CREATE TABLE contact_information (
  id SERIAL PRIMARY KEY,
  official_id INT REFERENCES elected_officials(id),
  phone_number VARCHAR(15),
  email VARCHAR(100)
);

CREATE TABLE government_positions (
  id SERIAL PRIMARY KEY,
  position_name VARCHAR(100) NOT NULL
);

CREATE TABLE federal_officials (
	federal_official_id INT PRIMARY KEY,
	office_location VARCHAR(100),
	party_affiliation VARCHAR(50),
	elected_official_id INT REFERENCES elected_officials(elected_official_id)
);

CREATE TABLE state_officials (
	state_official_id INT PRIMARY KEY,
	state_name VARCHAR(50),
	political_party VARCHAR(50),
	elected_official_id INT REFERENCES elected_officials(elected_official_id)
);

CREATE TABLE local_officials (
	local_official_id INT PRIMARY KEY,
	locality_name VARCHAR(50),
	address VARCHAR(100),
	elected_official_id INT REFERENCES elected_officials(elected_official_id)
);
