tables.table_schema:
CREATE TABLE legislative_bills (
  bill_id UUID PRIMARY KEY,
  bill_number INT,
  amendment_text TEXT,
  amendment_date TIMESTAMP
);

CREATE TABLE bill_history (
  history_id UUID PRIMARY KEY,
  bill_id UUID REFERENCES legislative_bills(bill_id),
  amendment_description TEXT,
  amendment_date TIMESTAMP
);

CREATE TABLE Bills (
    bill_id INT PRIMARY KEY,
    bill_name VARCHAR(255),
    amendment_count INT,
    date_proposed DATE
);

CREATE TABLE Authors (
    author_id INT PRIMARY KEY,
    author_name VARCHAR(255),
    author_email VARCHAR(255)
);

CREATE TABLE History (
    history_id INT PRIMARY KEY,
    bill_id INT,
    action VARCHAR(255),
    date_actioned DATE
);

CREATE TABLE Votes (
    vote_id INT PRIMARY KEY,
    bill_id INT,
    voter_id INT,
    vote_status VARCHAR(50)
);
