tables.table_schema:
CREATE TABLE laws (
    law_id INT PRIMARY KEY,
    law_name VARCHAR(255) NOT NULL,
    implementation_date DATE,
    impact_analysis TEXT
);

CREATE TABLE policy_monitoring (
    monitoring_id INT PRIMARY KEY,
    policy_id INT REFERENCES laws(law_id),
    monitoring_date DATE,
    monitoring_results TEXT
);

CREATE TABLE laws (
  law_id SERIAL PRIMARY KEY,
