tables.table_schema:
CREATE TABLE SurveyResults (
    survey_id UUID PRIMARY KEY,
    voter_id UUID,
    survey_date DATE,
    survey_response TEXT
);

CREATE TABLE survey_results (
    id INT PRIMARY KEY,
    voter_id INT,
    survey_question TEXT,
    survey_answer TEXT
);

CREATE TABLE voters (
    voter_id INT PRIMARY KEY,
    voter_name TEXT,
    age INT,
    location TEXT
);
