tables.table_schema:
CREATE TABLE bills (
  bill_id SERIAL PRIMARY KEY,
  bill_name VARCHAR(255) NOT NULL,
  proposed_date DATE,
  status VARCHAR(50)
);

CREATE TABLE resolutions (
  resolution_id SERIAL PRIMARY KEY,
  resolution_name VARCHAR(255) NOT NULL,
  passed_date DATE,
  resolved_by VARCHAR(50)
);

CREATE TABLE committees (
    committee_id INT PRIMARY KEY,
    committee_name VARCHAR(100),
    chair VARCHAR(100),
    members INT
);

CREATE TABLE committees (
    committee_id UUID PRIMARY KEY,
    committee_name TEXT,
    committee_chair TEXT,
    member_count INT
);
