tables.table_schema:
CREATE TABLE applicant_details (
    applicant_id INT PRIMARY KEY,
    applicant_name VARCHAR(255),
    applicant_email VARCHAR(255),
    application_date TIMESTAMP
);

CREATE TABLE application_status (
    status_id INT PRIMARY KEY,
    applicant_id INT,
    status VARCHAR(50),
    status_date TIMESTAMP,
    FOREIGN KEY (applicant_id) REFERENCES applicant_details(applicant_id)
);

CREATE TABLE citizenship_applications (
    applicant_id INT PRIMARY KEY,
    applicant_name VARCHAR(255),
    application_date DATE,
    status VARCHAR(50)
);

CREATE TABLE application_details (
    application_id INT PRIMARY KEY,
    applicant_id INT,
    document_type VARCHAR(100),
    document_details TEXT
);

CREATE TABLE status_updates (
    update_id INT PRIMARY KEY,
    application_id INT,
    update_date DATE,
    status_description TEXT
);

CREATE TABLE application_history (
    history_id INT PRIMARY KEY,
    application_id INT,
    history_date DATE,
    history_description TEXT
);

CREATE TABLE documents (
  document_id INT PRIMARY KEY,
  applicant_id INT REFERENCES applicants(applicant_id),
  document_type VARCHAR(50),
  document_url VARCHAR(255)
);

CREATE TABLE approvals (
  approval_id INT PRIMARY KEY,
  applicant_id INT REFERENCES applicants(applicant_id),
  approval_date DATE,
  approved_by VARCHAR(100)
);

CREATE TABLE rejections (
  rejection_id INT PRIMARY KEY,
  applicant_id INT REFERENCES applicants(applicant_id),
  rejection_date DATE,
  reason TEXT
);
