tables.table_schema:
CREATE TABLE citizenship_requirements (
  id INT PRIMARY KEY,
  residency_duration INT,
  language_proficiency VARCHAR
);

CREATE TABLE CitizenshipRequirements (
    id SERIAL PRIMARY KEY,
    residency_duration INT,
    language_proficiency VARCHAR(50)
);

CREATE TABLE ResidencyDuration (
    id SERIAL PRIMARY KEY,
    residency_type VARCHAR(50),
    minimum_duration INT
);

CREATE TABLE LanguageProficiency (
    id SERIAL PRIMARY KEY,
    language VARCHAR(50),
    proficiency_level VARCHAR(50)
);

CREATE TABLE requirements (
    id INT PRIMARY KEY,
    residency_duration INT NOT NULL,
    language_proficiency VARCHAR(50) NOT NULL
);

CREATE TABLE citizenship_documents (
    id INT PRIMARY KEY,
    document_name VARCHAR(100) NOT NULL,
    document_type VARCHAR(50) NOT NULL
);

CREATE TABLE applicants (
    id INT PRIMARY KEY,
    full_name VARCHAR(100) NOT NULL,
    residency_status VARCHAR(50) NOT NULL
);

CREATE TABLE citizenship_applications (
    id INT PRIMARY KEY,
    applicant_id INT NOT NULL,
    requirements_id INT NOT NULL,
    status VARCHAR(50) NOT NULL
);

CREATE TABLE language_proficiency_levels (
    id INT PRIMARY KEY,
    level_name VARCHAR(50) NOT NULL,
    proficiency_description TEXT
);
