tables.table_schema:
CREATE TABLE citizenship_laws (
    country VARCHAR(50),
    law_title VARCHAR(100),
    law_description TEXT,
    compliance_status BOOLEAN
);

CREATE TABLE citizenship_laws (
    id SERIAL PRIMARY KEY,
    country VARCHAR(255) NOT NULL,
    law_description TEXT,
    compliance_status BOOLEAN DEFAULT FALSE
);

CREATE TABLE compliance_tracking (
    tracking_id SERIAL PRIMARY KEY,
    law_id INT NOT NULL,
    law_compliance_date DATE,
    compliance_officer VARCHAR(255)
);

CREATE TABLE Citizenship Laws (

country_name VARCHAR(50),
legal_age INT,
citizenship_requirements TEXT
);

CREATE TABLE Citizenship Regulations (

law_id INT,
regulation_details TEXT,
compliance_date DATE
);

CREATE TABLE Compliance Tracking (

tracking_id INT,
country_id INT,
law_id INT,
compliance_status VARCHAR(20)
);

CREATE TABLE RegulatoryAuthorities (
	authority_id INT NOT NULL,
	authority_name VARCHAR(255) NOT NULL,
	authority_description TEXT,
	PRIMARY KEY (authority_id)
);
