CREATE TABLE citizenship_exam_questions (
  question_id INT PRIMARY KEY,
  question_text TEXT,
  correct_answer TEXT
);

CREATE TABLE citizenship_exam_answers (
  answer_id INT PRIMARY KEY,
  question_id INT,
  answer_text TEXT,
  FOREIGN KEY (question_id) REFERENCES citizenship_exam_questions(question_id)
);

