tables.table_schema:
CREATE TABLE dual_citizenship_regulations (
   id INT PRIMARY KEY,
   country_name VARCHAR(255),
   regulation TEXT
);

CREATE TABLE dual_citizenship (
  id SERIAL PRIMARY KEY,
  full_name VARCHAR(100) NOT NULL,
  date_of_birth DATE NOT NULL,
  nationality VARCHAR(50) NOT NULL
);

CREATE TABLE citizenship_regulations (
  id SERIAL PRIMARY KEY,
  country VARCHAR(50) NOT NULL,
  regulation TEXT NOT NULL
);

CREATE TABLE citizen_countries (
  id SERIAL PRIMARY KEY,
  citizen_id INT REFERENCES dual_citizenship(id),
  country VARCHAR(50) NOT NULL
);

CREATE TABLE dual_citizenship_status (
    person_id INT PRIMARY KEY,
    nationality_1 VARCHAR(50),
    nationality_2 VARCHAR(50),
    valid_until DATE
);

CREATE TABLE regulations (
    dual_citizenship_rule_id INT PRIMARY KEY,
    rule_description TEXT,
    rule_effective_date DATE
);

CREATE TABLE person_details (
    person_id INT PRIMARY KEY,
    full_name VARCHAR(100),
    date_of_birth DATE,
    gender VARCHAR(10)
);

CREATE TABLE country_details (
    country_id INT PRIMARY KEY,
    country_name VARCHAR(50),
    country_code CHAR(2)
);
