tables.table_schema:
CREATE TABLE tenant_payments (
    tenant_id INT,
    payment_date DATE,
    amount DECIMAL(10, 2)
);

CREATE TABLE rental_properties (
    property_id INT,
    property_name VARCHAR(50),
    monthly_rent DECIMAL(10, 2),
    status VARCHAR(20)
);

CREATE TABLE TenantPayments (
  tenant_id INT,
  payment_date DATE,
  amount DECIMAL(10, 2),
  payment_method VARCHAR(50)
);

CREATE TABLE Tenants (
  tenant_id INT,
  tenant_name VARCHAR(100),
  contact_number VARCHAR(15),
  email_address VARCHAR(100)
);

CREATE TABLE RentalProperties (
  property_id INT,
  property_address VARCHAR(255),
  rent_amount DECIMAL(10, 2),
  last_payment_date DATE
);

CREATE TABLE Tenant { 
    id INTEGER PRIMARY KEY, 
    name VARCHAR(50) NOT NULL, 
    email VARCHAR(100) NOT NULL, 
    phone VARCHAR(15) NOT NULL 
}

CREATE TABLE RentalProperty { 
    property_id INTEGER PRIMARY KEY, 
    address VARCHAR(100) NOT NULL, 
    city VARCHAR(50) NOT NULL, 
    state VARCHAR(2) NOT NULL 
}

CREATE TABLE PaymentRecord { 
    payment_id INTEGER PRIMARY KEY, 
    tenant_id INTEGER NOT NULL, 
    payment_date DATE NOT NULL, 
    amount DECIMAL(10, 2) NOT NULL 
}

CREATE TABLE CollectionStatus { 
    collection_id INTEGER PRIMARY KEY, 
    payment_id INTEGER NOT NULL, 
    status VARCHAR(20) NOT NULL, 
    collection_date DATE 
}

CREATE TABLE properties (
   property_id INT PRIMARY KEY,
   address VARCHAR(200),
   num_units INT,
   rental_amount DECIMAL(10, 2)
);

CREATE TABLE late_payments (
   payment_id INT PRIMARY KEY,
   tenant_id INT,
   property_id INT,
   late_fee DECIMAL(5, 2)
);

CREATE TABLE collection_agents (
   agent_id INT PRIMARY KEY,
   name VARCHAR(50),
   email VARCHAR(100),
   phone_number VARCHAR(15)
);
