tables.table_schema:
CREATE TABLE peak_rental_prices (
    rental_id INT PRIMARY KEY,
    rental_location VARCHAR(255),
    peak_season_price DECIMAL
);

CREATE TABLE off_peak_rental_prices (
    rental_id INT PRIMARY KEY,
    rental_location VARCHAR(255),
    off_peak_season_price DECIMAL
);

CREATE TABLE rental_prices (
    property_id INT,
    rental_price DECIMAL,
    rental_date DATE
);

CREATE TABLE peak_periods (
    period_id INT,
    start_date DATE,
    end_date DATE
);

CREATE TABLE off_peak_periods (
    period_id INT,
    start_date DATE,
    end_date DATE
);

CREATE TABLE peak_rental_prices( 
    property_id INT, 
    rental_price DECIMAL(10, 2), 
    peak_season_start DATE, 
    peak_season_end DATE 
);

CREATE TABLE off_peak_rental_prices( 
    property_id INT, 
    rental_price DECIMAL(10, 2), 
    off_peak_season_start DATE, 
    off_peak_season_end DATE 
);

CREATE TABLE properties( 
    property_id INT PRIMARY KEY, 
    property_name VARCHAR(255), 
    property_type VARCHAR(50), 
    location VARCHAR(100) 
);

CREATE TABLE seasons( 
    season_id INT PRIMARY KEY, 
    season_name VARCHAR(50), 
    start_date DATE, 
    end_date DATE 
);
