tables.table_schema:
CREATE TABLE rental_properties (
    property_id INT PRIMARY KEY,
    property_name TEXT,
    property_location GEOGRAPHY,
    property_price FLOAT
);

CREATE TABLE rental_trends (
    trend_id INT PRIMARY KEY,
    trend_date DATE,
    trend_location GEOGRAPHY,
    trend_description TEXT
);

CREATE TABLE RentalLocations (
  location_id INT PRIMARY KEY,
  location_name VARCHAR(100),
  latitude FLOAT,
  longitude FLOAT
);

CREATE TABLE RentalUsers (
  user_id INT PRIMARY KEY,
  user_name VARCHAR(100),
  email VARCHAR(255),
  phone_number VARCHAR(15)
);

CREATE TABLE rental_features (
    feature_id INT PRIMARY KEY,
    property_id INT REFERENCES rental_properties(property_id),
    feature_name TEXT NOT NULL
);

CREATE TABLE rental_ratings (
    rating_id INT PRIMARY KEY,
    property_id INT REFERENCES rental_properties(property_id),
    rating FLOAT NOT NULL,
    review TEXT
);

CREATE TABLE rental_images (
    image_id INT PRIMARY KEY,
    property_id INT REFERENCES rental_properties(property_id),
    image_url TEXT NOT NULL
);
