tables.table_schema:
CREATE TABLE InvestmentMetrics (
    investment_id INT PRIMARY KEY,
    sharpe_ratio FLOAT,
    beta FLOAT,
    standard_deviation FLOAT
);

CREATE TABLE InvestmentMetrics (
    investment_id UUID PRIMARY KEY,
    sharpe_ratio FLOAT,
    beta FLOAT,
    standard_deviation FLOAT
);

CREATE TABLE InvestmentReturns (
    return_id UUID PRIMARY KEY,
    investment_id UUID REFERENCES InvestmentMetrics(investment_id),
    return_value FLOAT,
    return_date DATE
);

CREATE TABLE risk_metrics (
  id INT PRIMARY KEY,
  sharpe_ratio DOUBLE,
  beta DOUBLE,
  std_dev DOUBLE
);

CREATE TABLE investment_performance (
  investment_id INT PRIMARY KEY,
  returns DOUBLE,
  volatility DOUBLE
);

CREATE TABLE investor_details (
  investor_id INT PRIMARY KEY,
  first_name VARCHAR(50),
  last_name VARCHAR(50),
  email VARCHAR(100)
);

CREATE TABLE SharpeRatio (
	risk_metric_id INT,
	sharpe_ratio FLOAT
);

CREATE TABLE Beta (
	risk_metric_id INT,
	company_name VARCHAR(255),
	beta FLOAT
);

CREATE TABLE StandardDeviation (
	risk_metric_id INT,
	investment_type VARCHAR(255),
	std_dev FLOAT
);

CREATE TABLE PerformanceEvaluation (
	risk_metric_id INT,
	evaluation_date DATE,
	performance_score FLOAT
);

CREATE TABLE Portfolio (
    id INT PRIMARY KEY,
    name VARCHAR(255) NOT NULL,
    capital DECIMAL(10,2) NOT NULL
);

CREATE TABLE MarketData (
    id INT PRIMARY KEY,
    stock_id INT NOT NULL,
    market_price DECIMAL(10,2) NOT NULL,
    date_recorded DATE
);
