tables.table_schema:
CREATE TABLE investor_profiles (
    investor_id INT PRIMARY KEY,
    first_name VARCHAR,
    last_name VARCHAR,
    email VARCHAR
);

CREATE TABLE investment_details (
    investment_id INT PRIMARY KEY,
    investor_id INT,
    risk_tolerance DECIMAL,
    investment_goal VARCHAR
);

CREATE TABLE investors (
  investor_id INT PRIMARY KEY,
  investor_name VARCHAR(50) NOT NULL,
  investor_email VARCHAR(100) UNIQUE NOT NULL
);

CREATE TABLE risk_tolerances (
  risk_tolerance_id INT PRIMARY KEY,
  risk_tolerance_level VARCHAR(50) NOT NULL,
  risk_tolerance_description TEXT
);

CREATE TABLE investment_goals (
  goal_id INT PRIMARY KEY,
  goal_name VARCHAR(50) NOT NULL,
  goal_description TEXT
);

CREATE TABLE risk_tolerances (
    risk_id INT PRIMARY KEY,
    investor_id INT,
    risk_profile VARCHAR(50),
    risk_score DECIMAL(10,2)
);

CREATE TABLE investment_goals (
    goal_id INT PRIMARY KEY,
    investor_id INT,
    goal_type VARCHAR(50),
    goal_description TEXT
);

CREATE TABLE investments (
    investment_id INT PRIMARY KEY,
    investor_id INT,
    investment_type VARCHAR(50),
    amount_invested DECIMAL(15,2)
);
