tables.table_schema:
CREATE TABLE preferences (
    customer_id UUID REFERENCES customers(customer_id),
    preference_name VARCHAR(50) NOT NULL,
    preference_value VARCHAR(255),
    PRIMARY KEY (customer_id, preference_name)
);

CREATE TABLE customers (
    customer_id INT PRIMARY KEY,
    name VARCHAR(100),
    email VARCHAR(100),
    phone_number VARCHAR(20)
);

CREATE TABLE preferences (
    preference_id INT PRIMARY KEY,
    customer_id INT REFERENCES customers(customer_id),
    preference_description TEXT
);

CREATE TABLE marketing_strategies (
    strategy_id INT PRIMARY KEY,
    strategy_name VARCHAR(100),
    target_preference_id INT REFERENCES preferences(preference_id),
    target_purchase_amount DECIMAL(10,2)
);

CREATE TABLE preferences (
    preference_id UUID PRIMARY KEY,
    customer_id UUID REFERENCES customers(id),
    preference_name VARCHAR(255),
    preference_value VARCHAR(255)
);

CREATE TABLE marketing_strategies (
    strategy_id UUID PRIMARY KEY,
    strategy_name VARCHAR(255),
    target_age_min INT,
    target_age_max INT
);

CREATE TABLE customer_strategies (
    customer_id UUID REFERENCES customers(id),
    strategy_id UUID REFERENCES marketing_strategies(strategy_id),
    UNIQUE(customer_id, strategy_id)
);
