tables.table_schema:
CREATE TABLE products (
    id INT PRIMARY KEY,
    name VARCHAR(255),
    category VARCHAR(255),
    price DECIMAL(10, 2)
);

CREATE TABLE orders (
    order_id INT PRIMARY KEY,
    product_id INT,
    quantity INT,
    total_price DECIMAL(10, 2),
    order_date DATE
);

CREATE TABLE high_end_furniture (
    id INT PRIMARY KEY,
    name VARCHAR(255) NOT NULL,
    price DECIMAL(10, 2) NOT NULL,
    quantity INT NOT NULL
);

CREATE TABLE staging_services (
    id INT PRIMARY KEY,
    service_type VARCHAR(255) NOT NULL,
    description TEXT,
    price DECIMAL(10, 2) NOT NULL
);

CREATE TABLE furniture_inventory (
    id INT PRIMARY KEY,
    furniture_id INT NOT NULL,
    quantity INT NOT NULL,
    FOREIGN KEY (furniture_id) REFERENCES high_end_furniture(id)
);
