tables.table_schema:
CREATE TABLE MarketTrends (
 report_id INT PRIMARY KEY,
 neighborhood VARCHAR(50),
 property_value DECIMAL(10,2),
 market_trend VARCHAR(100)
);

CREATE TABLE upscale_neighborhoods ( 
    neighborhood_id INT PRIMARY KEY, 
    neighborhood_name VARCHAR(50) NOT NULL, 
    median_property_value DECIMAL(15, 2) NOT NULL 
);

CREATE TABLE real_estate_investors ( 
    investor_id INT PRIMARY KEY, 
    investor_name VARCHAR(50) NOT NULL, 
    investment_amount DECIMAL(15, 2) NOT NULL 
);

CREATE TABLE MarketTrendsReports (
  report_id INT PRIMARY KEY,
  neighborhood VARCHAR(255),
  trend_analysis TEXT
);

CREATE TABLE PropertyValuesReports (
  report_id INT PRIMARY KEY,
  property_address VARCHAR(255),
  value_estimate DECIMAL(10, 2),
  market_analysis TEXT
);

CREATE TABLE Investors (
  investor_id INT PRIMARY KEY,
  investor_name VARCHAR(255),
  email VARCHAR(255)
);

CREATE TABLE Investors (
    investor_id INT PRIMARY KEY,
    investor_name VARCHAR(255),
    contact_email VARCHAR(255),
    phone_number VARCHAR(15)
);

CREATE TABLE Investments (
    investment_id INT PRIMARY KEY,
    investor_id INT,
    property_id INT,
    investment_amount DECIMAL
);

CREATE TABLE Neighborhoods (
    neighborhood_id INT PRIMARY KEY,
    neighborhood_name VARCHAR(255),
    upscale_score INT
);
