tables.table_schema:
CREATE TABLE luxury_home_sellers (
    seller_id INT PRIMARY KEY,
    seller_name VARCHAR(50),
    seller_email VARCHAR(100),
    seller_phone VARCHAR(15)
);

CREATE TABLE luxury_home_buyers (
    buyer_id INT PRIMARY KEY,
    buyer_name VARCHAR(50),
    buyer_email VARCHAR(100),
    buyer_phone VARCHAR(15)
);

CREATE TABLE luxury_homes (
    home_id INT PRIMARY KEY,
    home_address VARCHAR(100),
    home_price DECIMAL(10, 2),
    home_status VARCHAR(20)
);

CREATE TABLE users (
    user_id INT PRIMARY KEY,
    username VARCHAR(50),
    email VARCHAR(100),
    password_hash VARCHAR(255)
);
