tables.table_schema:
CREATE TABLE Customers (
    customer_id INT PRIMARY KEY,
    name VARCHAR(50),
    email VARCHAR(100),
    preferences TEXT
);

CREATE TABLE Notifications (
    notification_id INT PRIMARY KEY,
    customer_id INT,
    property_id INT,
    time_sent TIMESTAMP
);

CREATE TABLE notifications_log(
  log_id INT PRIMARY KEY,
  notification_id INT,
  log_message TEXT,
  log_date TIMESTAMP
);
