tables.table_schema:
CREATE TABLE virtual_tours (
    id UUID PRIMARY KEY,
    tour_name VARCHAR(255) NOT NULL,
    tour_description TEXT,
    property_id UUID REFERENCES luxury_properties(id)
);

CREATE TABLE clients (
    id UUID PRIMARY KEY,
    client_name VARCHAR(100) NOT NULL,
    email VARCHAR(255) UNIQUE NOT NULL,
    phone_number VARCHAR(15)
);

CREATE TABLE tour_interactions (
    id UUID PRIMARY KEY,
    client_id UUID REFERENCES clients(id),
    tour_id UUID REFERENCES virtual_tours(id),
    interaction_date TIMESTAMP
);

CREATE TABLE property (
  property_id INT PRIMARY KEY,
  property_name VARCHAR(255),
  property_location VARCHAR(255),
  property_price DECIMAL
);

CREATE TABLE feedback (
  feedback_id INT PRIMARY KEY,
  tour_id INT,
  feedback_text TEXT,
  rating INT
);

CREATE TABLE virtual_reality (
  vr_id INT PRIMARY KEY,
  tour_id INT,
  vr_technology VARCHAR(50)
);
