CREATE TABLE rental_units (
    unit_id INT PRIMARY KEY,
    unit_name VARCHAR(50) NOT NULL,
    unit_type VARCHAR(20),
    area_sqft INT
);

CREATE TABLE RentalUnit (
  unit_id INT PRIMARY KEY,
  unit_name VARCHAR(50) NOT NULL,
  unit_size FLOAT NOT NULL,
 monthly_rent DECIMAL(10, 2) NOT NULL
);
