tables.table_schema:
CREATE TABLE property_management_staff_permissions (
    staff_id INT,
    staff_name VARCHAR(50),
    property_id INT,
    access_level VARCHAR(50)
);

CREATE TABLE property_managers (
    id INT PRIMARY KEY,
    name VARCHAR(50) NOT NULL,
    property_id INT NOT NULL,
    role VARCHAR(50) NOT NULL
);

CREATE TABLE property_access (
    access_id INT PRIMARY KEY,
    property_id INT NOT NULL,
    user_id INT NOT NULL,
    access_level VARCHAR(50) NOT NULL
);

CREATE TABLE property_managers (
    manager_id INT PRIMARY KEY,
    manager_name VARCHAR(50),
    manager_phone VARCHAR(15),
    manager_email VARCHAR(50)
);

CREATE TABLE properties (
    property_id INT PRIMARY KEY,
    property_name VARCHAR(50),
    property_address VARCHAR(100),
    property_type VARCHAR(50)
);

CREATE TABLE access_roles (
    role_id INT PRIMARY KEY,
    role_name VARCHAR(50),
    role_description VARCHAR(100)
);

CREATE TABLE permissions (
    permission_id INT PRIMARY KEY,
    permission_name VARCHAR(50),
    permission_description VARCHAR(100)
);

CREATE TABLE property_management_staff (
    staff_id INT PRIMARY KEY,
    staff_name VARCHAR(50),
    staff_role VARCHAR(50)
);

CREATE TABLE property_permissions (
    permission_id INT PRIMARY KEY,
    permission_name VARCHAR(50)
);

CREATE TABLE staff_permissions (
    staff_id INT,
    permission_id INT,
    FOREIGN KEY (staff_id) REFERENCES property_management_staff(staff_id),
    FOREIGN KEY (permission_id) REFERENCES property_permissions(permission_id)
);

CREATE TABLE role_permissions (
    staff_role VARCHAR(50),
    permission_id INT,
    FOREIGN KEY (permission_id) REFERENCES property_permissions(permission_id)
);

CREATE TABLE user_roles (
    staff_id INT,
    staff_role VARCHAR(50),
    FOREIGN KEY (staff_id) REFERENCES property_management_staff(staff_id)
);
