tables.table_schema:
CREATE TABLE sales_data (
    sale_id INT PRIMARY KEY,
    property_address VARCHAR(255),
    sale_date DATE,
    sale_amount DECIMAL
);

CREATE TABLE market_trends (
    trend_id INT PRIMARY KEY,
    trend_date DATE,
    trend_data JSON
);

CREATE TABLE market_trends (
	trend_id INT PRIMARY KEY,
	trend_date DATE,
	median_property_value DECIMAL(10,2),
	sales_volume INT
);

CREATE TABLE Sales (
    sale_id INT,
    property_id INT,
    sale_date DATE,
    sale_price DECIMAL(10,2)
);

CREATE TABLE Properties (
    property_id INT,
    address VARCHAR(255),
    year_built INT,
    property_type VARCHAR(50)
);

CREATE TABLE Agents (
    agent_id INT,
    name VARCHAR(100),
    phone_number VARCHAR(15),
    email VARCHAR(100)
);

CREATE TABLE Buyers (
    buyer_id INT,
    name VARCHAR(100),
    phone_number VARCHAR(15),
    email VARCHAR(100)
);

CREATE TABLE MarketTrends (
    trend_id INT,
    trend_date DATE,
    average_price DECIMAL(10,2),
    inventory_count INT
);
