tables.table_schema:
CREATE TABLE Buildings (
    building_id INT PRIMARY KEY,
    building_name VARCHAR(255) NOT NULL,
    address VARCHAR(255) NOT NULL
);

CREATE TABLE EnergyConsumption (
    reading_id SERIAL PRIMARY KEY,
    building_id INT REFERENCES Buildings(building_id),
    timestamp TIMESTAMP NOT NULL,
    energy_usage DECIMAL(10,2) NOT NULL
);

CREATE TABLE Building (
  building_id INT PRIMARY KEY,
  building_name VARCHAR(50) NOT NULL,
  address VARCHAR(100) NOT NULL,
  estimated_capacity INT
);

CREATE TABLE Buildings (
	building_id INT PRIMARY KEY,
	building_name VARCHAR,
	building_address VARCHAR,
	building_size INT
);

CREATE TABLE EnergyConsumption (
	consumption_id INT PRIMARY KEY,
	building_id INT,
	consumption_date DATE,
	consumption_value DECIMAL
);

CREATE TABLE Reports (
	report_id INT PRIMARY KEY,
	building_id INT,
	report_date DATE,
	report_content TEXT
);

CREATE TABLE EnergyEfficiency (
	efficiency_id INT PRIMARY KEY,
	building_id INT,
	efficiency_date DATE,
	efficiency_rating DECIMAL
);

CREATE TABLE buildings (
    building_id INT PRIMARY KEY,
    building_name TEXT,
    address TEXT
);

CREATE TABLE energy_meters (
    meter_id INT PRIMARY KEY,
    building_id INT,
    meter_type TEXT,
    FOREIGN KEY (building_id) REFERENCES buildings(building_id)
);

CREATE TABLE users (
    user_id INT PRIMARY KEY,
    username TEXT,
    password TEXT
);
