tables.table_schema:
CREATE TABLE waste_collection (
  id INT PRIMARY KEY,
  date DATE,
  waste_type VARCHAR(50),
  weight DECIMAL
);

CREATE TABLE recycling_trends (
  trend_id INT PRIMARY KEY,
  trend_date DATE,
  recycling_category VARCHAR(50),
  recycling_rate DECIMAL
);

CREATE TABLE waste_statistics (
  stat_id INT PRIMARY KEY,
  stat_date DATE,
  waste_type ENUM('Recyclable', 'Organic', 'Non-Recyclable'),
  total_weight_kg DECIMAL(15, 2)
);
