tables.table_schema:
CREATE TABLE SustainableAgriculturePractices (
    id INT PRIMARY KEY,
    crop_name VARCHAR(255),
    crop_rotation_period INT,
    farming_method VARCHAR(255)
);

CREATE TABLE CropRotation (
    id INT PRIMARY KEY,
    crop_name TEXT NOT NULL,
    rotation_cycle INT NOT NULL,
    last_rotated_date DATE
);

CREATE TABLE CropRotation (
    id INT PRIMARY KEY,
    crop_name VARCHAR(255),
    rotation_years INT,
    last_rotation_date DATE
);

CREATE TABLE OrganicFarmingMethods (
    id INT PRIMARY KEY,
    method_name VARCHAR(255),
    description TEXT,
    certification_status VARCHAR(50)
);

CREATE TABLE CropRotation (
    id SERIAL PRIMARY KEY,
    crop_name VARCHAR(100) NOT NULL,
    rotation_period INTEGER,
    last_rotated_date DATE
);

CREATE TABLE OrganicFarming (
    id SERIAL PRIMARY KEY,
    crop_name VARCHAR(100) NOT NULL,
    organic_certified BOOLEAN,
    certification_date DATE
);

CREATE TABLE CropHarvest (
    id SERIAL PRIMARY KEY,
    crop_name VARCHAR(100) NOT NULL,
    harvest_date DATE,
    quantity INTEGER
);

CREATE TABLE FarmEquipment (
    id SERIAL PRIMARY KEY,
    equipment_name VARCHAR(100) NOT NULL,
    equipment_type VARCHAR(50),
    purchase_date DATE
);

CREATE TABLE crop_rotation (
    id INT PRIMARY KEY,
    crop_name VARCHAR(255),
    rotation_sequence INT
);

CREATE TABLE farming_methods (
    id INT PRIMARY KEY,
    method_name VARCHAR(255),
    method_description TEXT
);

CREATE TABLE farmer (
    id INT PRIMARY KEY,
    name VARCHAR(255),
    location VARCHAR(255)
);

CREATE TABLE farm (
    id INT PRIMARY KEY,
    farm_name VARCHAR(255),
    size_acres DECIMAL(10, 2)
);

CREATE TABLE farm_practices (
    farm_id INT,
    crop_rotation_id INT,
    farming_method_id INT
);
