tables.table_schema:
CREATE TABLE emissions_data (
  id SERIAL PRIMARY KEY,
  vehicle_type TEXT NOT NULL,
  emissions DECIMAL NOT NULL
);

CREATE TABLE travel_modes (
  id SERIAL PRIMARY KEY,
  mode_name TEXT NOT NULL,
  description TEXT
);

CREATE TABLE travel_routes (
  id SERIAL PRIMARY KEY,
  route_name TEXT NOT NULL,
  distance DECIMAL NOT NULL,
  emissions_saved DECIMAL
);

CREATE TABLE eco_strategies (
  id SERIAL PRIMARY KEY,
  strategy_name TEXT NOT NULL,
  description TEXT
);

CREATE TABLE transportation_modes (
    mode_id INT PRIMARY KEY,
    mode_name VARCHAR(255) NOT NULL,
    emissions_factor FLOAT NOT NULL
);

CREATE TABLE users (
    user_id INT PRIMARY KEY,
    user_name VARCHAR(255) NOT NULL,
    email VARCHAR(255) NOT NULL
);

CREATE TABLE eco_strategies (
    strategy_id INT PRIMARY KEY,
    strategy_name VARCHAR(255) NOT NULL,
    description TEXT
);
