tables.table_schema:
CREATE TABLE renewable_energy_sources (
    source_id INT PRIMARY KEY,
    source_name VARCHAR(255) NOT NULL,
    source_type VARCHAR(50) NOT NULL,
    capacity FLOAT NOT NULL
);

CREATE TABLE clean_energy_adoption (
    adoption_id INT PRIMARY KEY,
    region_name VARCHAR(100) NOT NULL,
    technology VARCHAR(50) NOT NULL,
    adoption_percentage FLOAT NOT NULL
);

CREATE TABLE RenewableEnergySources (
    id INT PRIMARY KEY,
    source_name VARCHAR(50) NOT NULL,
    region VARCHAR(50) NOT NULL,
    capacity FLOAT NOT NULL
);

CREATE TABLE EnergyAdoption (
    id INT PRIMARY KEY,
    technology_name VARCHAR(50) NOT NULL,
    region VARCHAR(50) NOT NULL,
    adoption_rate FLOAT NOT NULL
);

CREATE TABLE RegionStatistics (
    id INT PRIMARY KEY,
    region VARCHAR(50) NOT NULL,
    population INT NOT NULL,
    energy_consumption FLOAT NOT NULL
);

CREATE TABLE clean_energy_technologies (
    tech_id INT PRIMARY KEY,
    tech_name VARCHAR(50),
    tech_description TEXT
);

CREATE TABLE region_data (
    region_id INT PRIMARY KEY,
    region_name VARCHAR(50),
    avg_annual_energy_consumption DECIMAL,
    total_installed_capacity DECIMAL
);
