tables.table_schema:
CREATE TABLE ForestCover (
  forest_id INT PRIMARY KEY,
  area_acres FLOAT,
  tree_species VARCHAR(255),
  plant_date DATE
);

CREATE TABLE forest_cover (
 id INT PRIMARY KEY,
 area DECIMAL,
 time TIMESTAMP
);

CREATE TABLE reforestation_projects (
 project_id INT PRIMARY KEY,
 location VARCHAR,
 start_date DATE,
 end_date DATE
);

CREATE TABLE forest_monitoring (
 monitoring_id INT PRIMARY KEY,
 project_id INT,
 area_check DECIMAL,
 date DATE
);

CREATE TABLE ForestCoverChange (
    id INT PRIMARY KEY,
    area_name VARCHAR(255),
    year INT,
    forest_coverage DECIMAL(10, 2)
);

CREATE TABLE ReforestationProjects (
    project_id INT PRIMARY KEY,
    project_name VARCHAR(255),
    start_date DATE,
    end_date DATE
);

CREATE TABLE AssessmentResults (
    result_id INT PRIMARY KEY,
    project_id INT,
    assessment_date DATE,
    success_rate DECIMAL(5, 2)
);

CREATE TABLE ForestRegion (
    region_id INT PRIMARY KEY,
    region_name VARCHAR(255) NOT NULL
);

CREATE TABLE ForestParcel (
    parcel_id INT PRIMARY KEY,
    region_id INT REFERENCES ForestRegion(region_id),
    parcel_area DECIMAL NOT NULL
);

CREATE TABLE TreeSpecies (
    species_id INT PRIMARY KEY,
    species_name VARCHAR(255) NOT NULL
);

CREATE TABLE TreeInventory (
    inventory_id INT PRIMARY KEY,
    parcel_id INT REFERENCES ForestParcel(parcel_id),
    species_id INT REFERENCES TreeSpecies(species_id),
    quantity INT NOT NULL
);

CREATE TABLE ReforestationProjects (
    project_id INT PRIMARY KEY,
    start_date DATE NOT NULL,
    end_date DATE,
    region_id INT REFERENCES ForestRegion(region_id)
);
